// Easter Egg - Konami Code for Admin Access
// Non-obvious way to navigate between main site and admin site

class EasterEgg {
  constructor() {
    this.konamiCode = [
      'ArrowUp', 'ArrowUp', 'ArrowDown', 'ArrowDown',
      'ArrowLeft', 'ArrowRight', 'ArrowLeft', 'ArrowRight',
      'KeyB', 'KeyA'
    ];
    this.userInput = [];
    this.isActive = false;
    this.timeout = null;

    this.init();
  }

  init() {
    // Listen for keydown events
    document.addEventListener('keydown', (e) => this.handleKeyPress(e));

    // Add subtle visual indicator that something is listening
    this.addVisualIndicator();
  }

  handleKeyPress(event) {
    // Clear timeout if user is actively typing
    if (this.timeout) {
      clearTimeout(this.timeout);
    }

    // Add current key to sequence
    this.userInput.push(event.code);

    // Keep only the last 10 keys (length of Konami code)
    if (this.userInput.length > this.konamiCode.length) {
      this.userInput.shift();
    }

    // Check if sequence matches Konami code
    if (this.checkSequence()) {
      this.activateEasterEgg();
    }

    // Reset sequence after 3 seconds of inactivity
    this.timeout = setTimeout(() => {
      this.userInput = [];
    }, 3000);
  }

  checkSequence() {
    if (this.userInput.length !== this.konamiCode.length) {
      return false;
    }

    for (let i = 0; i < this.userInput.length; i++) {
      const userKey = this.userInput.at(i);
      const expectedKey = this.konamiCode.at(i);
      if (userKey !== expectedKey) {
        return false;
      }
    }
    return true;
  }

  activateEasterEgg() {
    if (this.isActive) {
      return;
    }

    this.isActive = true;
    this.userInput = [];

    // Show activation feedback
    this.showActivationFeedback();

    // Navigate to admin site after a brief delay
    setTimeout(() => {
      this.navigateToAdmin();
    }, 1500);
  }

  showActivationFeedback() {
    // Create a subtle notification
    const notification = document.createElement('div');
    notification.className = 'easter-egg-notification';
    notification.innerHTML = `
            <div class="easter-egg-content">
                <span class="easter-egg-icon">üéÆ</span>
                <span class="easter-egg-text">Access Granted</span>
            </div>
        `;

    // Add styles
    notification.style.cssText = `
            position: fixed;
            top: 20px;
            right: 20px;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            color: white;
            padding: 15px 20px;
            border-radius: 10px;
            box-shadow: 0 10px 25px rgba(0,0,0,0.2);
            z-index: 10000;
            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
            font-size: 14px;
            font-weight: 500;
            opacity: 0;
            transform: translateX(100%);
            transition: all 0.3s ease;
            cursor: pointer;
        `;

    // Add content styles
    const content = notification.querySelector('.easter-egg-content');
    content.style.cssText = `
            display: flex;
            align-items: center;
            gap: 10px;
        `;

    const icon = notification.querySelector('.easter-egg-icon');
    icon.style.cssText = `
            font-size: 18px;
        `;

    const text = notification.querySelector('.easter-egg-text');
    text.style.cssText = `
            font-weight: 600;
        `;

    // Add to page
    document.body.appendChild(notification);

    // Animate in
    setTimeout(() => {
      notification.style.opacity = '1';
      notification.style.transform = 'translateX(0)';
    }, 100);

    // Add click to dismiss
    notification.addEventListener('click', () => {
      this.dismissNotification(notification);
    });

    // Auto-dismiss after 3 seconds
    setTimeout(() => {
      this.dismissNotification(notification);
    }, 3000);
  }

  dismissNotification(notification) {
    notification.style.opacity = '0';
    notification.style.transform = 'translateX(100%)';

    setTimeout(() => {
      if (notification.parentNode) {
        notification.parentNode.removeChild(notification);
      }
    }, 300);
  }

  navigateToAdmin() {
    // Determine the admin URL based on current domain with validation
    const currentHost = window.location.hostname;
    let adminUrl;

    // Validate and sanitize the hostname
    const allowedHosts = [
      'robertconsulting.net',
      'www.robertconsulting.net',
      'admin.robertconsulting.net',
      'localhost',
      '127.0.0.1'
    ];

    if (!allowedHosts.includes(currentHost) && !currentHost.includes('localhost') && !currentHost.includes('127.0.0.1')) {
      console.error('Invalid hostname for admin navigation');
      return;
    }

    if (currentHost === 'robertconsulting.net' || currentHost === 'www.robertconsulting.net') {
      adminUrl = 'https://admin.robertconsulting.net';
    } else if (currentHost === 'admin.robertconsulting.net') {
      // If already on admin, go back to main site
      adminUrl = 'https://robertconsulting.net';
    } else {
      // Fallback for local development - use safe defaults
      adminUrl = currentHost.includes('admin')
        ? 'http://localhost:3000'
        : 'http://admin.localhost:3000';
    }

    // Validate the final URL before navigation
    try {
      const url = new URL(adminUrl);
      // Use the url variable to avoid no-new error
      if (!url) return;
    } catch (e) {
      console.error('Invalid admin URL constructed');
      return;
    }

    // Show navigation message
    this.showNavigationMessage(adminUrl);

    // Navigate after a brief delay
    setTimeout(() => {
      window.location.href = adminUrl;
    }, 2000);
  }

  showNavigationMessage(url) {
    const message = document.createElement('div');
    message.className = 'easter-egg-navigation';
    message.innerHTML = `
            <div class="easter-egg-nav-content">
                <span class="easter-egg-nav-icon">üöÄ</span>
                <span class="easter-egg-nav-text">Navigating to ${url.includes('admin') ? 'Admin Panel' : 'Main Site'}...</span>
            </div>
        `;

    message.style.cssText = `
            position: fixed;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            background: rgba(0, 0, 0, 0.9);
            color: white;
            padding: 20px 30px;
            border-radius: 15px;
            box-shadow: 0 20px 40px rgba(0,0,0,0.3);
            z-index: 10001;
            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
            font-size: 16px;
            font-weight: 500;
            opacity: 0;
            transition: all 0.3s ease;
        `;

    const content = message.querySelector('.easter-egg-nav-content');
    content.style.cssText = `
            display: flex;
            align-items: center;
            gap: 15px;
        `;

    const icon = message.querySelector('.easter-egg-nav-icon');
    icon.style.cssText = `
            font-size: 24px;
            animation: pulse 1s infinite;
        `;

    // Add pulse animation
    const style = document.createElement('style');
    style.textContent = `
            @keyframes pulse {
                0%, 100% { transform: scale(1); }
                50% { transform: scale(1.1); }
            }
        `;
    document.head.appendChild(style);

    document.body.appendChild(message);

    // Animate in
    setTimeout(() => {
      message.style.opacity = '1';
    }, 100);
  }

  addVisualIndicator() {
    // Add a very subtle indicator that something is listening
    // This could be a small dot or icon that's barely visible
    const indicator = document.createElement('div');
    indicator.className = 'easter-egg-indicator';
    indicator.innerHTML = '‚óè';

    indicator.style.cssText = `
            position: fixed;
            bottom: 20px;
            right: 20px;
            color: rgba(102, 126, 234, 0.3);
            font-size: 8px;
            z-index: 9999;
            pointer-events: none;
            transition: opacity 0.3s ease;
        `;

    document.body.appendChild(indicator);

    // Make it slightly more visible on hover
    indicator.addEventListener('mouseenter', () => {
      indicator.style.opacity = '0.6';
    });

    indicator.addEventListener('mouseleave', () => {
      indicator.style.opacity = '0.3';
    });
  }
}

// Initialize the easter egg when DOM is loaded
document.addEventListener('DOMContentLoaded', () => {
  const easterEgg = new EasterEgg();
  // Store reference to avoid no-new error
  window.easterEggInstance = easterEgg;
});

// Export for potential external use
window.EasterEgg = EasterEgg;

/**
 * Automated CloudFront Cache Invalidation
 * This script can be integrated into your deployment process
 */

const AWS = require('aws-sdk');

class CloudFrontInvalidator {
  constructor() {
    this.cloudfront = new AWS.CloudFront();
    this.distributionId = 'E36DBYPHUUKB3V'; // Your distribution ID
  }

  /**
     * Invalidate specific paths
     * @param {string[]} paths - Array of paths to invalidate
     * @returns {Promise<Object>} - Invalidation result
     */
  async invalidatePaths(paths = ['/*']) {
    try {
      const params = {
        DistributionId: this.distributionId,
        InvalidationBatch: {
          Paths: {
            Quantity: paths.length,
            Items: paths
          },
          CallerReference: `auto-invalidate-${Date.now()}`
        }
      };

      const result = await this.cloudfront.createInvalidation(params).promise();
      console.log('‚úÖ CloudFront invalidation created:', result.Invalidation.Id);
      return result;
    } catch (error) {
      console.error('‚ùå Failed to invalidate CloudFront cache:', error);
      throw error;
    }
  }

  /**
     * Invalidate all files
     * @returns {Promise<Object>} - Invalidation result
     */
  async invalidateAll() {
    return this.invalidatePaths(['/*']);
  }

  /**
     * Invalidate specific files
     * @param {string[]} files - Array of file paths to invalidate
     * @returns {Promise<Object>} - Invalidation result
     */
  async invalidateFiles(files) {
    const paths = files.map(file => `/${file}`);
    return this.invalidatePaths(paths);
  }

  /**
     * Check invalidation status
     * @param {string} invalidationId - Invalidation ID to check
     * @returns {Promise<Object>} - Invalidation status
     */
  async checkInvalidationStatus(invalidationId) {
    try {
      const params = {
        DistributionId: this.distributionId,
        Id: invalidationId
      };

      const result = await this.cloudfront.getInvalidation(params).promise();
      return result.Invalidation;
    } catch (error) {
      console.error('‚ùå Failed to check invalidation status:', error);
      throw error;
    }
  }
}

// Export for use in other scripts
module.exports = CloudFrontInvalidator;

// CLI usage example
if (require.main === module) {
  const invalidator = new CloudFrontInvalidator();

  const args = process.argv.slice(2);
  const command = args[0];

  switch (command) {
  case 'all':
    invalidator.invalidateAll()
      .then(result => console.log('Invalidation created:', result.Invalidation.Id))
      .catch(error => console.error('Error:', error));
    break;

  case 'files': {
    const files = args.slice(1);
    invalidator.invalidateFiles(files)
      .then(result => console.log('Invalidation created:', result.Invalidation.Id))
      .catch(error => console.error('Error:', error));
    break;
  }

  case 'status': {
    const invalidationId = args[1];
    invalidator.checkInvalidationStatus(invalidationId)
      .then(status => console.log('Status:', status.Status))
      .catch(error => console.error('Error:', error));
    break;
  }

  default:
    console.log('Usage:');
    console.log('  node auto-invalidate.js all');
    console.log('  node auto-invalidate.js files index.html learning.html');
    console.log('  node auto-invalidate.js status <distribution-id>');
  }
}

// Enhanced Admin Authentication Lambda Function
// Handles MFA, session management, IP restrictions, and audit logging

const AWS = require('aws-sdk');
const crypto = require('crypto');
const bcrypt = require('bcryptjs');
const speakeasy = require('speakeasy');

const secretsManager = new AWS.SecretsManager();
const dynamodb = new AWS.DynamoDB.DocumentClient();

// Configuration
const SECRETS_MANAGER_SECRET_ID = process.env.SECRETS_MANAGER_SECRET_ID;
const SESSIONS_TABLE_NAME = process.env.SESSIONS_TABLE_NAME;
const AUDIT_TABLE_NAME = process.env.AUDIT_TABLE_NAME;
const MFA_ENABLED = process.env.MFA_ENABLED === 'true';

let securityConfig = null;

// Cache security configuration
async function getSecurityConfig() {
  if (!securityConfig) {
    try {
      const result = await secretsManager.getSecretValue({
        SecretId: SECRETS_MANAGER_SECRET_ID
      }).promise();

      securityConfig = JSON.parse(result.SecretString);
    } catch (error) {
      console.error('Error retrieving security config:', error);
      throw new Error('Security configuration unavailable');
    }
  }
  return securityConfig;
}

// Generate secure session token
function generateSessionToken() {
  return crypto.randomBytes(32).toString('hex');
}

// Generate MFA secret for user (currently unused but kept for future MFA implementation)
// function generateMFASecret() {
//   return speakeasy.generateSecret({
//     name: 'Robert Consulting Admin',
//     issuer: 'Robert Consulting'
//   });
// }

// Verify MFA token
function verifyMFAToken(token, secret) {
  return speakeasy.totp.verify({
    secret: secret,
    encoding: 'base32',
    token: token,
    window: 2
  });
}

// Check if IP is allowed
function isIPAllowed(clientIP, allowedIPs) {
  if (!allowedIPs || allowedIPs.length === 0) {
    return true; // No IP restrictions
  }

  return allowedIPs.some(allowedIP => {
    if (allowedIP.includes('/')) {
      // CIDR notation
      return isIPInCIDR(clientIP, allowedIP);
    } else {
      // Single IP
      return clientIP === allowedIP;
    }
  });
}

// Simple CIDR check (for basic use cases)
function isIPInCIDR(ip, cidr) {
  const [network, prefixLength] = cidr.split('/');
  const ipNum = ipToNumber(ip);
  const networkNum = ipToNumber(network);
  const mask = (0xffffffff << (32 - parseInt(prefixLength, 10))) >>> 0;

  return (ipNum & mask) === (networkNum & mask);
}

function ipToNumber(ip) {
  return ip.split('.').reduce((acc, octet) => (acc << 8) + parseInt(octet, 10), 0) >>> 0;
}

// Log audit event
async function logAuditEvent(eventType, details, clientIP, userAgent) {
  try {
    const timestamp = new Date().toISOString();
    const actionId = crypto.randomUUID();

    await dynamodb.put({
      TableName: AUDIT_TABLE_NAME,
      Item: {
        timestamp: timestamp,
        actionId: actionId,
        actionType: eventType,
        userIp: clientIP,
        userAgent: userAgent,
        details: JSON.stringify(details),
        expiresAt: Math.floor(Date.now() / 1000) + (365 * 24 * 60 * 60) // 1 year
      }
    }).promise();
  } catch (error) {
    console.error('Error logging audit event:', error);
  }
}

// Check for brute force attempts
async function checkBruteForceAttempts(clientIP) {
  try {
    const config = await getSecurityConfig();
    const cutoffTime = new Date(Date.now() - (config.lockoutDuration * 60 * 1000));

    const result = await dynamodb.query({
      TableName: AUDIT_TABLE_NAME,
      IndexName: 'user-ip-index',
      KeyConditionExpression: 'userIp = :ip',
      FilterExpression: 'action_type = :type AND #timestamp > :cutoff',
      ExpressionAttributeNames: {
        '#timestamp': 'timestamp'
      },
      ExpressionAttributeValues: {
        ':ip': clientIP,
        ':type': 'LOGIN_FAILED',
        ':cutoff': cutoffTime.toISOString()
      }
    }).promise();

    return result.Items.length >= config.max_login_attempts;
  } catch (error) {
    console.error('Error checking brute force attempts:', error);
    return false;
  }
}

// Create session
async function createSession(clientIP, userAgent) {
  try {
    const config = await getSecurityConfig();
    const sessionToken = generateSessionToken();
    const expiresAt = Math.floor(Date.now() / 1000) + (config.session_timeout * 60);

    await dynamodb.put({
      TableName: SESSIONS_TABLE_NAME,
      Item: {
        sessionId: sessionToken,
        createdAt: new Date().toISOString(),
        userIp: clientIP,
        userAgent: userAgent,
        expiresAt: expiresAt,
        lastActivity: new Date().toISOString()
      }
    }).promise();

    return sessionToken;
  } catch (error) {
    console.error('Error creating session:', error);
    throw new Error('Session creation failed');
  }
}

// Validate session
async function validateSession(sessionToken, clientIP) {
  try {
    const result = await dynamodb.get({
      TableName: SESSIONS_TABLE_NAME,
      Key: {
        sessionId: sessionToken,
        createdAt: {$gte: new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString()}
      }
    }).promise();

    if (!result.Item) {
      return false;
    }

    // Check if session is expired
    if (Date.now() / 1000 > result.Item.expiresAt) {
      await dynamodb.delete({
        TableName: SESSIONS_TABLE_NAME,
        Key: {
          sessionId: sessionToken,
          createdAt: result.Item.createdAt
        }
      }).promise();
      return false;
    }

    // Check IP match
    if (result.Item.userIp !== clientIP) {
      await logAuditEvent('SESSION_IP_MISMATCH', {
        sessionId: sessionToken,
        expectedIp: result.Item.userIp,
        actualIp: clientIP
      }, clientIP, '');
      return false;
    }

    // Update last activity
    await dynamodb.update({
      TableName: SESSIONS_TABLE_NAME,
      Key: {
        sessionId: sessionToken,
        createdAt: result.Item.createdAt
      },
      UpdateExpression: 'SET lastActivity = :activity',
      ExpressionAttributeValues: {
        ':activity': new Date().toISOString()
      }
    }).promise();

    return true;
  } catch (error) {
    console.error('Error validating session:', error);
    return false;
  }
}

// Main handler
exports.handler = async(event) => {
  const request = event.Records[0].cf.request;
  const headers = request.headers;
  const uri = request.uri;
  const method = request.method;

  // Extract client information
  let clientIP = 'unknown';
  if (headers['x-forwarded-for']) {
    clientIP = headers['x-forwarded-for'][0].value.split(',')[0].trim();
  } else if (headers['cf-connecting-ip']) {
    clientIP = headers['cf-connecting-ip'][0].value;
  }

  const userAgent = headers['user-agent'] ? headers['user-agent'][0].value : 'unknown';

  try {
    const config = await getSecurityConfig();

    // Check IP restrictions
    if (!isIPAllowed(clientIP, config.allowedIps)) {
      await logAuditEvent('IP_BLOCKED', {
        clientIp: clientIP,
        allowedIps: config.allowedIps
      }, clientIP, userAgent);

      return {
        status: '403',
        statusDescription: 'Forbidden',
        headers: {
          'content-type': [{key: 'Content-Type', value: 'text/plain'}]
        },
        body: 'Access denied: IP not allowed'
      };
    }

    // Skip authentication for static assets
    if (uri.match(/\.(css|js|png|jpg|jpeg|gif|ico|svg|woff|woff2|ttf|eot)$/)) {
      return request;
    }

    // Handle login endpoint
    if (uri === '/admin-login' && method === 'POST') {
      return await handleLogin(request, clientIP, userAgent, config);
    }

    // Handle MFA verification
    if (uri === '/admin-mfa' && method === 'POST') {
      return await handleMFAVerification(request, clientIP, userAgent, config);
    }

    // Check for existing session
    const sessionCookie = getCookie(headers, 'admin-session');
    if (sessionCookie && await validateSession(sessionCookie, clientIP)) {
      return request;
    }

    // Redirect to login page
    return {
      status: '302',
      statusDescription: 'Found',
      headers: {
        location: [{
          key: 'Location',
          value: '/admin-login.html'
        }],
        'cache-control': [{
          key: 'Cache-Control',
          value: 'no-cache, no-store, must-revalidate'
        }]
      }
    };

  } catch (error) {
    console.error('Authentication error:', error);

    await logAuditEvent('AUTH_ERROR', {
      error: error.message,
      uri: uri,
      method: method
    }, clientIP, userAgent);

    return {
      status: '500',
      statusDescription: 'Internal Server Error',
      headers: {
        'content-type': [{key: 'Content-Type', value: 'text/plain'}]
      },
      body: 'Authentication service unavailable'
    };
  }
};

// Handle login
async function handleLogin(request, clientIP, userAgent, config) {
  try {
    // Check for brute force attempts
    if (await checkBruteForceAttempts(clientIP)) {
      await logAuditEvent('BRUTE_FORCE_BLOCKED', {
        clientIp: clientIP,
        lockoutDuration: config.lockoutDuration
      }, clientIP, userAgent);

      return {
        status: '429',
        statusDescription: 'Too Many Requests',
        headers: {
          'content-type': [{key: 'Content-Type', value: 'application/json'}],
          'retry-after': [{key: 'Retry-After', value: config.lockoutDuration.toString()}]
        },
        body: JSON.stringify({
          error: 'Too many failed attempts',
          retryAfter: config.lockoutDuration
        })
      };
    }

    // Parse request body
    const body = Buffer.from(request.body.data, 'base64').toString();
    const credentials = JSON.parse(body);

    // Verify password
    const passwordValid = await bcrypt.compare(credentials.password, config.admin_password_hash);

    if (!passwordValid) {
      await logAuditEvent('LOGIN_FAILED', {
        reason: 'invalid_password',
        username: credentials.username
      }, clientIP, userAgent);

      return {
        status: '401',
        statusDescription: 'Unauthorized',
        headers: {
          'content-type': [{key: 'Content-Type', value: 'application/json'}]
        },
        body: JSON.stringify({
          error: 'Invalid credentials',
          mfaRequired: MFA_ENABLED
        })
      };
    }

    // If MFA is enabled, require MFA verification
    if (MFA_ENABLED) {
      await logAuditEvent('LOGIN_SUCCESS_PENDING_MFA', {
        username: credentials.username
      }, clientIP, userAgent);

      return {
        status: '200',
        statusDescription: 'OK',
        headers: {
          'content-type': [{key: 'Content-Type', value: 'application/json'}]
        },
        body: JSON.stringify({
          success: true,
          mfaRequired: true,
          message: 'MFA verification required'
        })
      };
    }

    // Create session
    const sessionToken = await createSession(clientIP, userAgent);

    await logAuditEvent('LOGIN_SUCCESS', {
      username: credentials.username,
      sessionId: sessionToken
    }, clientIP, userAgent);

    return {
      status: '200',
      statusDescription: 'OK',
      headers: {
        'content-type': [{key: 'Content-Type', value: 'application/json'}],
        'set-cookie': [{
          key: 'Set-Cookie',
          value: `admin-session=${sessionToken}; HttpOnly; Secure; SameSite=Strict; Max-Age=${config.session_timeout * 60}`
        }]
      },
      body: JSON.stringify({
        success: true,
        redirect: '/admin/'
      })
    };

  } catch (error) {
    console.error('Login error:', error);

    await logAuditEvent('LOGIN_ERROR', {
      error: error.message
    }, clientIP, userAgent);

    return {
      status: '500',
      statusDescription: 'Internal Server Error',
      headers: {
        'content-type': [{key: 'Content-Type', value: 'application/json'}]
      },
      body: JSON.stringify({
        error: 'Login service unavailable'
      })
    };
  }
}

// Handle MFA verification
async function handleMFAVerification(request, clientIP, userAgent, config) {
  try {
    const body = Buffer.from(request.body.data, 'base64').toString();
    const mfaData = JSON.parse(body);

    // Verify MFA token
    const mfaValid = verifyMFAToken(mfaData.token, config.mfa_secret_key);

    if (!mfaValid) {
      await logAuditEvent('MFA_FAILED', {
        token: mfaData.token
      }, clientIP, userAgent);

      return {
        status: '401',
        statusDescription: 'Unauthorized',
        headers: {
          'content-type': [{key: 'Content-Type', value: 'application/json'}]
        },
        body: JSON.stringify({
          error: 'Invalid MFA token'
        })
      };
    }

    // Create session
    const sessionToken = await createSession(clientIP, userAgent);

    await logAuditEvent('MFA_SUCCESS', {
      sessionId: sessionToken
    }, clientIP, userAgent);

    return {
      status: '200',
      statusDescription: 'OK',
      headers: {
        'content-type': [{key: 'Content-Type', value: 'application/json'}],
        'set-cookie': [{
          key: 'Set-Cookie',
          value: `admin-session=${sessionToken}; HttpOnly; Secure; SameSite=Strict; Max-Age=${config.session_timeout * 60}`
        }]
      },
      body: JSON.stringify({
        success: true,
        redirect: '/admin/'
      })
    };

  } catch (error) {
    console.error('MFA verification error:', error);

    await logAuditEvent('MFA_ERROR', {
      error: error.message
    }, clientIP, userAgent);

    return {
      status: '500',
      statusDescription: 'Internal Server Error',
      headers: {
        'content-type': [{key: 'Content-Type', value: 'application/json'}]
      },
      body: JSON.stringify({
        error: 'MFA service unavailable'
      })
    };
  }
}

// Helper function to get cookie value
function getCookie(headers, name) {
  const cookieHeader = headers.cookie;
  if (!cookieHeader) {
    return null;
  }

  const cookies = cookieHeader[0].value.split(';');
  for (const cookie of cookies) {
    const [key, value] = cookie.trim().split('=');
    if (key === name) {
      return value;
    }
  }
  return null;
}

/**
 * AWS Lambda function for processing contact form submissions
 * Sends emails via AWS SES
 */

const {SESClient, SendEmailCommand} = require('@aws-sdk/client-ses');
const ses = new SESClient({region: 'us-east-1'});

// HTML escaping function to prevent XSS
function escapeHtml(text) {
  if (typeof text !== 'string') {
    return '';
  }

  return text
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#039;');
}

exports.handler = async(event) => {
  // Enable CORS
  const headers = {
    'Access-Control-Allow-Origin': '*',
    'Access-Control-Allow-Headers': 'Content-Type',
    'Access-Control-Allow-Methods': 'POST, OPTIONS',
    'Content-Type': 'application/json'
  };

  // Handle preflight OPTIONS request
  if (event.httpMethod === 'OPTIONS') {
    return {
      statusCode: 200,
      headers,
      body: JSON.stringify({message: 'CORS preflight'})
    };
  }

  try {
    // Parse the request body
    const body = JSON.parse(event.body);
    const {name, email, subject, message} = body;

    // Validate required fields
    if (!name || !email || !subject || !message) {
      return {
        statusCode: 400,
        headers,
        body: JSON.stringify({
          error: 'Missing required fields',
          required: ['name', 'email', 'subject', 'message']
        })
      };
    }

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      return {
        statusCode: 400,
        headers,
        body: JSON.stringify({error: 'Invalid email format'})
      };
    }

    // Create email content
    const emailParams = {
      Source: 'info@robertconsulting.net',
      Destination: {
        ToAddresses: ['info@robertconsulting.net']
      },
      Message: {
        Subject: {
          Data: `Contact Form: ${subject}`,
          Charset: 'UTF-8'
        },
        Body: {
          Html: {
            Data: `
                            <html>
                                <body>
                                    <h2>New Contact Form Submission</h2>
                                    <p><strong>Name:</strong> ${escapeHtml(name)}</p>
                                    <p><strong>Email:</strong> ${escapeHtml(email)}</p>
                                    <p><strong>Subject:</strong> ${escapeHtml(subject)}</p>
                                    <p><strong>Message:</strong></p>
                                    <p>${escapeHtml(message).replace(/\n/g, '<br>')}</p>
                                    <hr>
                                    <p><em>Sent from robertconsulting.net contact form</em></p>
                                </body>
                            </html>
                        `,
            Charset: 'UTF-8'
          },
          Text: {
            Data: `
New Contact Form Submission

Name: ${name}
Email: ${email}
Subject: ${subject}

Message:
${message}

---
Sent from robertconsulting.net contact form
                        `,
            Charset: 'UTF-8'
          }
        }
      }
    };

    // Send email via SES
    const command = new SendEmailCommand(emailParams);
    const result = await ses.send(command);

    // Log successful submission
    console.log('Email sent successfully:', result.MessageId);

    return {
      statusCode: 200,
      headers,
      body: JSON.stringify({
        message: 'Email sent successfully',
        messageId: result.MessageId
      })
    };

  } catch (error) {
    console.error('Error sending email:', error);

    return {
      statusCode: 500,
      headers,
      body: JSON.stringify({
        error: 'Failed to send email',
        details: error.message
      })
    };
  }
};

