/**
 * Test data integrity with strict validation
 */
function testDataIntegrity(data) {
  console.log('\nüîç Testing Data Integrity (Strict Mode)...');

  if (!data) {
    testResults.dataIntegrity.failed++;
    testResults.dataIntegrity.tests.push('‚ùå No data received');
    return;
  }

  // Test for null/undefined values in critical fields
  const criticalFields = [
    'aws.monthlyCostTotal',
    'health.site.status',
    'performance.resourceTiming.ttfb',
    'github.commits.last7Days'
  ];

  let hasNullValues = false;
  criticalFields.forEach(field => {
    let value;
    const fieldParts = field.split('.');
    if (fieldParts.length === 1) {
      switch (fieldParts[0]) {
        case 'aws':
          value = data.aws;
          break;
        case 'traffic':
          value = data.traffic;
          break;
        case 'health':
          value = data.health;
          break;
        case 'performance':
          value = data.performance;
          break;
        case 'github':
          value = data.github;
          break;
        case 'velocity':
          value = data.velocity;
          break;
        default:
          value = undefined;
      }
    } else if (fieldParts.length === 2) {
      const [parent, child] = fieldParts;
      let parentObj;
      switch (parent) {
        case 'aws':
          parentObj = data.aws;
          break;
        case 'traffic':
          parentObj = data.traffic;
          break;
        case 'health':
          parentObj = data.health;
          break;
        case 'performance':
          parentObj = data.performance;
          break;
        case 'github':
          parentObj = data.github;
          break;
        case 'velocity':
          parentObj = data.velocity;
          break;
        default:
          parentObj = undefined;
      }
      if (parentObj) {
        switch (child) {
          case 'monthlyCostTotal':
            value = parentObj.monthlyCostTotal;
            break;
          case 'services':
            value = parentObj.services;
            break;
          case 'totalRequests':
            value = parentObj.totalRequests;
            break;
          case 'last7Days':
            value = parentObj.last7Days;
            break;
          case 'last30Days':
            value = parentObj.last30Days;
            break;
          default:
            value = undefined;
        }
      } else {
        value = undefined;
      }
    } else {
      value = undefined;
    }
    if (value === null || value === undefined) {
      testResults.dataIntegrity.failed++;
      testResults.dataIntegrity.tests.push(`‚ùå Critical field is null/undefined: ${field}`);
      hasNullValues = true;
    }
  });

  if (!hasNullValues) {
    testResults.dataIntegrity.passed++;
    testResults.dataIntegrity.tests.push('‚úÖ No null/undefined values in critical fields');
  }

  // Test for reasonable data ranges
  if (data.aws && data.aws.monthlyCostTotal > 0 && data.aws.monthlyCostTotal < 1000) {
    testResults.dataIntegrity.passed++;
    testResults.dataIntegrity.tests.push('‚úÖ Cost data is in reasonable range');
  } else {
    testResults.dataIntegrity.failed++;
    testResults.dataIntegrity.tests.push(`‚ùå Cost data is unreasonable: $${data.aws?.monthlyCostTotal || 'missing'}`);
  }
}

