  }

  init() {
    console.log('üìö Initializing Best Practices Manager...');

    // Set up interactive features
    this.setupChecklistInteractivity();
    this.setupSmoothScrolling();
    this.setupProgressTracking();
    this.setupCodeExamples();

    console.log('‚úÖ Best Practices Manager initialized');
  }

  setupChecklistInteractivity() {
    const checkboxes = document.querySelectorAll('.checklist-items input[type="checkbox"]');

    checkboxes.forEach(checkbox => {
      checkbox.addEventListener('change', () => {
        this.updateChecklistProgress();
        this.saveChecklistData();
        this.showProgressNotification();
      });
    });
  }

  setupSmoothScrolling() {
    // Smooth scrolling for anchor links
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
      anchor.addEventListener('click', function(e) {
        e.preventDefault();
        const target = document.querySelector(this.getAttribute('href'));
        if (target) {
          target.scrollIntoView({
            behavior: 'smooth',
            block: 'start'
          });
        }
      });
    });
  }

  setupProgressTracking() {
    // Update progress on page load
    this.updateChecklistProgress();

    // Update progress periodically
    setInterval(() => {
      this.updateChecklistProgress();
    }, 5000);
  }

  setupCodeExamples() {
    // Add copy functionality to code examples
    const codeExamples = document.querySelectorAll('.code-example');

    codeExamples.forEach(example => {
      const copyButton = document.createElement('button');
      copyButton.className = 'copy-button';
      copyButton.innerHTML = 'üìã Copy';
      copyButton.style.cssText = `
                position: absolute;
                top: 0.5rem;
                right: 0.5rem;
                background: #38a169;
                color: white;
                border: none;
                padding: 0.25rem 0.5rem;
                border-radius: 4px;
                font-size: 0.75rem;
                cursor: pointer;
                opacity: 0;
                transition: opacity 0.2s ease;
            `;

      example.style.position = 'relative';
      example.appendChild(copyButton);

      // Show copy button on hover
      example.addEventListener('mouseenter', () => {
        copyButton.style.opacity = '1';
      });

      example.addEventListener('mouseleave', () => {
        copyButton.style.opacity = '0';
      });

      // Copy functionality
      copyButton.addEventListener('click', async() => {
        const code = example.querySelector('pre').textContent;
        try {
          await navigator.clipboard.writeText(code);
          copyButton.innerHTML = '‚úÖ Copied!';
          setTimeout(() => {
            copyButton.innerHTML = 'üìã Copy';
          }, 2000);
        } catch (err) {
          console.error('Failed to copy code:', err);
        }
      });
    });
  }

  updateChecklistProgress() {
    const categories = document.querySelectorAll('.checklist-category');

    categories.forEach(category => {
      const checkboxes = category.querySelectorAll('input[type="checkbox"]');
      const checkedBoxes = category.querySelectorAll('input[type="checkbox"]:checked');
      const progress = (checkedBoxes.length / checkboxes.length) * 100;

      // Create or update progress bar
      let progressBar = category.querySelector('.progress-bar');
      if (!progressBar) {
        progressBar = document.createElement('div');
        progressBar.className = 'progress-bar';
        category.appendChild(progressBar);
      }

      let progressFill = progressBar.querySelector('.progress-fill');
      if (!progressFill) {
        progressFill = document.createElement('div');
        progressFill.className = 'progress-fill';
        progressBar.appendChild(progressFill);
      }

      progressFill.style.width = `${progress}%`;

      // Update category title with progress
      const title = category.querySelector('h3');
      const originalTitle = title.textContent.replace(/\s*\(\d+%\)$/, '');
      title.textContent = `${originalTitle} (${Math.round(progress)}%)`;
    });
  }

  saveChecklistData() {
    const checkboxes = document.querySelectorAll('.checklist-items input[type="checkbox"]');
    const checklistState = {};

    checkboxes.forEach(checkbox => {
      checklistState[checkbox.id] = checkbox.checked;
    });

    localStorage.setItem('best-practices-checklist', JSON.stringify(checklistState));
  }

  loadChecklistData() {
    try {
      const saved = localStorage.getItem('best-practices-checklist');
      if (saved) {
        const checklistState = JSON.parse(saved);

        // Restore checkbox states
        Object.entries(checklistState).forEach(([id, checked]) => {
          const checkbox = document.getElementById(id);
          if (checkbox) {
            checkbox.checked = checked;
          }
        });

        return checklistState;
      }
    } catch (error) {
      console.warn('Failed to load checklist data:', error);
    }

    return {};
  }

  showProgressNotification() {
    const totalCheckboxes = document.querySelectorAll('.checklist-items input[type="checkbox"]').length;
    const checkedCheckboxes = document.querySelectorAll('.checklist-items input[type="checkbox"]:checked').length;
    const progress = Math.round((checkedCheckboxes / totalCheckboxes) * 100);

    // Create notification
    const notification = document.createElement('div');
    notification.className = 'progress-notification';
    notification.innerHTML = `
            <div style="
                position: fixed;
                top: 20px;
                right: 20px;
                background: #38a169;
                color: white;
                padding: 1rem 1.5rem;
                border-radius: 8px;
                box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
                z-index: 1000;
                font-weight: 600;
                animation: slideInRight 0.3s ease-out;
            ">
                üìä Progress: ${progress}% Complete
            </div>
        `;

    document.body.appendChild(notification);

    // Remove notification after 3 seconds
    setTimeout(() => {
      notification.style.animation = 'slideOutRight 0.3s ease-out';
      setTimeout(() => {
        document.body.removeChild(notification);
      }, 300);
    }, 3000);
  }

  // Export checklist data
  exportChecklist() {
    const totalCheckboxes = document.querySelectorAll('.checklist-items input[type="checkbox"]').length;
    const checkedCheckboxes = document.querySelectorAll('.checklist-items input[type="checkbox"]:checked').length;
    const progress = Math.round((checkedCheckboxes / totalCheckboxes) * 100);

    const exportData = {
      timestamp: new Date().toISOString(),
      progress: progress,
      completed: checkedCheckboxes,
      total: totalCheckboxes,
      categories: {}
    };

    // Get progress by category
    document.querySelectorAll('.checklist-category').forEach(category => {
      const categoryName = category.querySelector('h3').textContent.replace(/\s*\(\d+%\)$/, '');
      const checkboxes = category.querySelectorAll('input[type="checkbox"]');
      const checked = category.querySelectorAll('input[type="checkbox"]:checked');
      const categoryProgress = Math.round((checked.length / checkboxes.length) * 100);

      const categoryData = {
        progress: categoryProgress,
        completed: checked.length,
        total: checkboxes.length
      };
      // Use safe property assignment
      if (exportData.categories) {
        // Use safe property assignment based on category name
        if (categoryName === 'Security') {
          exportData.categories.Security = categoryData;
        } else if (categoryName === 'Performance') {
          exportData.categories.Performance = categoryData;
        } else if (categoryName === 'Accessibility') {
          exportData.categories.Accessibility = categoryData;
        } else if (categoryName === 'SEO') {
          exportData.categories.SEO = categoryData;
        } else if (categoryName === 'Code Quality') {
          exportData.categories['Code Quality'] = categoryData;
        } else {
          // For unknown categories, use a safe approach with additional validation
          if (categoryName && typeof categoryName === 'string' && categoryName.length < 50) {
            // Additional validation for unknown categories
            if (categoryName.match(/^[a-zA-Z0-9\s\-_]+$/)) {
              // Use safe property assignment for unknown categories
              switch (categoryName) {
                case 'Security':
                  exportData.categories.Security = categoryData;
                  break;
                case 'Performance':
                  exportData.categories.Performance = categoryData;
                  break;
                case 'Accessibility':
                  exportData.categories.Accessibility = categoryData;
                  break;
                case 'SEO':
                  exportData.categories.SEO = categoryData;
                  break;
                case 'Code Quality':
                  exportData.categories['Code Quality'] = categoryData;
                  break;
                default:
                  // For truly unknown categories, use a safe fallback with additional validation
                  if (exportData.categories && typeof exportData.categories === 'object') {
                    // Additional security validation for unknown categories
                    if (categoryName && typeof categoryName === 'string' && 
                        categoryName.length > 0 && categoryName.length < 50 &&
                        /^[a-zA-Z0-9\s\-_]+$/.test(categoryName)) {
                      // Use Object.defineProperty for safer assignment
                      try {
                        Object.defineProperty(exportData.categories, categoryName, {
                          value: categoryData,
                          writable: true,
                          enumerable: true,
                          configurable: true
                        });
                      } catch (error) {
                        console.warn('Failed to assign category data safely:', error);
                      }
                    }
                  }
              }
            }
          }
        }
      }
    });

    // Download as JSON
    const blob = new Blob([JSON.stringify(exportData, null, 2)], {type: 'application/json'});
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `best-practices-progress-${new Date().toISOString().split('T')[0]}.json`;
    a.click();
    URL.revokeObjectURL(url);
  }

  // Reset checklist
  resetChecklist() {
    if (confirm('Are you sure you want to reset all checklist items?')) {
      document.querySelectorAll('.checklist-items input[type="checkbox"]').forEach(checkbox => {
        checkbox.checked = false;
      });
      this.updateChecklistProgress();
      this.saveChecklistData();
      this.showProgressNotification();
    }
  }
}

