        details: {
          progress: `${Math.floor(60 + Math.random() * 40)}%`,
          estimatedTime: `${Math.floor(1 + Math.random() * 5)} minutes`
        }
      },
      {
        stage: 'monitoring',
        status: 'green',
        details: {
          responseTime: `${Math.floor(100 + Math.random() * 100)}ms`,
          errorRate: `${(Math.random() * 0.1).toFixed(2)}%`
        }
      }
    ];

    return updates[Math.floor(Math.random() * updates.length)];
  }

  /**
     * Handle WebSocket messages
     */
  onWebSocketMessage(event) {
    console.log('üì° WebSocket message received:', event);

    if (event.type === 'pipeline_update') {
      this.notifyListeners('pipeline_update', event.data);
    } else if (event.type === 'status_change') {
      this.notifyListeners('status_change', event.data);
    }
  }

  /**
     * Add event listener
     */
  addEventListener(event, callback) {
    if (!this.eventListeners.has(event)) {
      this.eventListeners.set(event, []);
    }
    this.eventListeners.get(event).push(callback);
  }

  /**
     * Remove event listener
     */
  removeEventListener(event, callback) {
    if (this.eventListeners.has(event)) {
      const listeners = this.eventListeners.get(event);
      const index = listeners.indexOf(callback);
      if (index > -1) {
        listeners.splice(index, 1);
      }
    }
  }

  /**
     * Notify listeners
     */
  notifyListeners(event, data) {
    if (this.eventListeners.has(event)) {
      this.eventListeners.get(event).forEach(callback => {
        try {
          callback(data);
        } catch (error) {
          console.error('Error in event listener:', error);
        }
      });
    }
  }

  /**
     * Get cached data
     */
  getCachedData(key) {
    const cached = this.cache.get(key);
    if (cached && Date.now() - cached.timestamp < this.cacheTimeout) {
      return cached.data;
    }
    return null;
  }

  /**
     * Set cached data
     */
  setCachedData(key, data) {
    this.cache.set(key, {
      data,
      timestamp: Date.now()
    });
  }

  /**
     * Simulate API delay
     */
  async simulateDelay(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
     * Cleanup resources
     */
  destroy() {
    if (this.wsConnection) {
      this.wsConnection.close();
    }
    this.eventListeners.clear();
    this.cache.clear();
  }
}

