    } else {
      actions = ['Check status', 'Review logs'];
    }
    return actions;
  }

  /**
     * Calculate health score
     */
  calculateHealthScore(stageStatuses) {
    const weights = {
      development: 0.15,
      testing: 0.20,
      staging: 0.15,
      security: 0.20,
      deployment: 0.15,
      monitoring: 0.15
    };

    let totalScore = 0;
    let totalWeight = 0;

    Object.entries(stageStatuses).forEach(([stage, status]) => {
      let weight;
      switch (stage) {
        case 'development':
          weight = weights.development || 0;
          break;
        case 'staging':
          weight = weights.staging || 0;
          break;
        case 'production':
          weight = weights.production || 0;
          break;
        default:
          weight = 0;
      }
      const score = this.getStatusScore(status);
      totalScore += score * weight;
      totalWeight += weight;
    });

    return totalWeight > 0 ? Math.round(totalScore / totalWeight) : 0;
  }

  /**
     * Get numeric score for status
     */
  getStatusScore(status) {
    // const scores = { // Unused for now - replaced with switch statement
    //   green: 100,
    //   yellow: 60,
    //   red: 0,
    //   unknown: 30
    // };
    let score;
    switch (status) {
      case 'green':
        score = 100;
        break;
      case 'yellow':
        score = 60;
        break;
      case 'red':
        score = 20;
        break;
      case 'unknown':
        score = 30;
        break;
      default:
        score = 0;
    }
    return score;
  }

  /**
     * Get status trend
     */
  getStatusTrend(currentStatuses, previousStatuses) {
    const trends = {};

    Object.keys(currentStatuses).forEach(stage => {
      let currentStatus, previousStatus;
      switch (stage) {
        case 'development':
          currentStatus = currentStatuses.development;
          previousStatus = previousStatuses.development || 'unknown';
          break;
        case 'staging':
          currentStatus = currentStatuses.staging;
          previousStatus = previousStatuses.staging || 'unknown';
          break;
        case 'production':
          currentStatus = currentStatuses.production;
          previousStatus = previousStatuses.production || 'unknown';
          break;
        default:
          currentStatus = 'unknown';
          previousStatus = 'unknown';
      }

      const current = this.getStatusScore(currentStatus);
      const previous = this.getStatusScore(previousStatus);

      if (current > previous) {
        switch (stage) {
          case 'development':
            trends.development = 'improving';
            break;
          case 'staging':
            trends.staging = 'improving';
            break;
          case 'production':
            trends.production = 'improving';
            break;
        }
      } else if (current < previous) {
        switch (stage) {
          case 'development':
            trends.development = 'declining';
            break;
          case 'staging':
            trends.staging = 'declining';
            break;
          case 'production':
            trends.production = 'declining';
            break;
        }
      } else {
        switch (stage) {
          case 'development':
            trends.development = 'stable';
            break;
          case 'staging':
            trends.staging = 'stable';
            break;
          case 'production':
            trends.production = 'stable';
            break;
        }
      }
    });

    return trends;
  }

  /**
     * Validate stage data
     */
  validateStageData(stage, data) {
    // const requiredFields = { // Unused for now - replaced with switch statement
    //   development: ['tests', 'conflicts', 'lastCommit'],
    //   testing: ['coverage', 'tests'],
    //   staging: ['uptime', 'health'],
    //   security: ['vulnerabilities', 'dependencies', 'compliance'],
    //   deployment: ['status', 'verification'],
    //   monitoring: ['uptime', 'responseTime', 'errorRate', 'alerts']
    // };

    let fields;
    switch (stage) {
      case 'development':
        fields = ['tests', 'conflicts', 'lastCommit'];
        break;
      case 'testing':
        fields = ['coverage', 'tests'];
        break;
      case 'staging':
        fields = ['uptime', 'health'];
        break;
      case 'security':
        fields = ['vulnerabilities', 'dependencies', 'compliance'];
        break;
      case 'deployment':
        fields = ['status', 'verification'];
        break;
      case 'monitoring':
        fields = ['uptime', 'responseTime', 'errorRate', 'alerts'];
        break;
      default:
        fields = [];
    }
    const missing = fields.filter(field => !(field in data));

    if (missing.length > 0) {
      console.warn(`Missing required fields for ${stage}:`, missing);
      return false;
    }

    return true;
  }
}

