      this.updateElement(`${elementId}-mobile-grade`, data.mobile.grade);
    }
    if (data.desktop) {
      this.updateElement(`${elementId}-desktop-score`, data.desktop.score);
      this.updateElement(`${elementId}-desktop-grade`, data.desktop.grade);
    }
    if (data.dns) {
