    echo -e "   ${BLUE}Name Servers:${NC} $name_servers"
  fi
  
  # List key records
  echo -e "   ${BLUE}Key Records:${NC}"
  aws route53 list-resource-record-sets --hosted-zone-id "$zone_id" \
    --query "ResourceRecordSets[?Type=='A' || Type=='AAAA' || Type=='CNAME' || Type=='NS'].{Name:Name,Type:Type,Value:ResourceRecords[0].Value || AliasTarget.DNSName}" \
    --output table 2>/dev/null | head -15 || echo "   (Could not list records)"
  
  # Check if this zone is in Terraform state
  if terraform state list 2>/dev/null | grep -q "aws_route53_zone.main"; then
    terraform_zone_id=$(terraform state show aws_route53_zone.main 2>/dev/null | grep "id\s*=" | head -1 | awk '{print $3}' | tr -d '"' || echo "")
    if [ "$zone_id" = "$terraform_zone_id" ]; then
      echo -e "   ${GREEN}‚úÖ This zone is in Terraform state${NC}"
    else
      echo -e "   ${YELLOW}‚ö†Ô∏è  This zone is NOT in Terraform state${NC}"
    fi
  fi
  
  echo ""
