function handler(event) {
  var req = event.request;
  var headers = req.headers;
  var uri = req.uri;

  // Skip authentication for login page and static assets
  if (uri === '/admin-login.html' || 
      uri === '/admin-login' || 
      uri === '/admin-mfa' ||
      uri.match(/\.(css|js|png|jpg|jpeg|gif|ico|svg|woff|woff2|ttf|eot)$/)) {
    return req;
  }

  // Check for session cookie
  var sessionCookie = getCookie(headers, 'admin-session');
  if (sessionCookie) {
    // Session exists, allow access (session validation handled by Lambda@Edge)
    return req;
  }

  // No session, redirect to login
  return {
    statusCode: 302,
    statusDescription: 'Found',
    headers: {
      'location': { value: '/admin-login.html' },
      'cache-control': { value: 'no-cache, no-store, must-revalidate' }
    }
  };
}

function getCookie(headers, name) {
  var cookieHeader = headers.cookie;
  if (!cookieHeader) return null;

  var cookies = cookieHeader.value.split(';');
  for (var i = 0; i < cookies.length; i++) {
    var cookie = cookies[i].trim();
    if (cookie.indexOf(name + '=') === 0) {
      return cookie.substring(name.length + 1);
    }
  }
  return null;
}

function handler(event) {
    var request = event.request;
    var querystring = request.querystring;
    var uri = request.uri;

    // Check if this is a staging request
    if (request.headers.host && request.headers.host.value.includes('staging.robertconsulting.net')) {
        // Allow static assets without secret
        if (uri.includes('/css/') || uri.includes('/js/') || 
            uri.endsWith('.css') || uri.endsWith('.js') || 
            uri.endsWith('.ico') || uri.endsWith('.png') || 
            uri.endsWith('.jpg') || uri.endsWith('.jpeg') || 
            uri.endsWith('.gif') || uri.endsWith('.svg') || 
            uri.endsWith('.woff') || uri.endsWith('.woff2') || 
            uri.endsWith('.ttf') || uri.endsWith('.eot')) {
            return request;
        }

        // Check for secret parameter
        if (querystring.secret && querystring.secret.value === 'staging-access-2025') {
            return request;
        }

        // Return access denied
        return {
            statusCode: 403,
            statusDescription: 'Forbidden',
            headers: {
                'content-type': { value: 'text/html' }
            },
            body: {
                encoding: 'text',
                data: '<!DOCTYPE html><html><head><title>Staging Access Required</title></head><body><h1>Staging Access Required</h1><p>Add ?secret=staging-access-2025 to your URL</p></body></html>'
            }
        };
    }

    return request;
}

function handler(event) {
  var req = event.request;
  var headers = req.headers;

  var authHeader = headers.authorization && headers.authorization.value;
  var expected = 'Basic YmFpbGV5X2FkbWluOmR1bW15';

  if (!authHeader || authHeader !== expected) {
    return {
      statusCode: 401,
      statusDescription: 'Unauthorized',
      headers: { 'www-authenticate': { value: 'Basic realm="Bailey Lessons Admin"' } },
      body: 'Authentication required'
    };
  }
  return req;
}

